mapper.select:
select u.name cname, sum(CASE when score=5 then 1 else 0 end) satisfy2, sum(CASE when score=4 then 1 else 0 end) satisfy1, sum(CASE when score=3 then 1 else 0 end) satisfy, sum(CASE when score=2 then 1 else 0 end) basic, sum(CASE when score=1 then 1 else 0 end) notsatisfy, sum(CASE when score=0 then 1 else 0 end) no from evaluate e INNER JOIN sys_user u on u.guid=e.userid INNER JOIN window w on e.winid=w.guid
where 1=1
and e.addtime >= #{starttime}
and #{endtime} >= e.addtime
and e.orgid=#{orgid}
group by u.guid,u.name ORDER BY u.name

 ,'1' flag from evaluate e
where to_days(e.addtime) = to_days(now())

	and e.orgid=#{orgid}

select d.name,count(n.type) cnt,d.guid type from dic d
left JOIN num_info n on n.type=d.guid and n.isused=0
where n.orgid=#{orgid}
GROUP BY d.name,d.guid;

SELECT i.`name` Name,i.idcard IDCardNo,IFNULL(d.nickname,c.`name`) DeptName,i.childname ServiceName,i.allnum TicketNO, IFNULL(DATE_FORMAT(i.addtime,'%Y-%m-%d %H:%i:%S'),'') PrintTime,IFNULL(DATE_FORMAT(i.starttime,'%Y-%m-%d %H:%i:%S'),'') StartTime, IFNULL(DATE_FORMAT(i.endtime,'%Y-%m-%d %H:%i:%S'),'') FinishedTime,i.`status` ProcessingStatus,i.winname CounterName FROM num_info i LEFT JOIN dic d ON i.type = d.guid LEFT JOIN dic_code c ON i.type = c.guid WHERE i.num > 0
and i.orgid=#{orgid}
AND TO_DAYS(i.addtime) = TO_DAYS(NOW()) ORDER BY i.num,i.addtime

SELECT i.`name` Name,i.idcard IDCardNo,IFNULL(d.nickname,c.`name`) DeptName,i.childname ServiceName,i.allnum TicketNO, IFNULL(DATE_FORMAT(i.addtime,'%Y-%m-%d %H:%i:%S'),'') PrintTime,IFNULL(DATE_FORMAT(i.starttime,'%Y-%m-%d %H:%i:%S'),'') StartTime, IFNULL(DATE_FORMAT(i.endtime,'%Y-%m-%d %H:%i:%S'),'') FinishedTime,i.`status` ProcessingStatus,i.winname CounterName FROM num_info_all i LEFT JOIN dic d ON i.type = d.guid LEFT JOIN dic_code c ON i.type = c.guid WHERE i.num > 0
and i.addtime >=#{starttime}
and #{endtime}>=i.addtime
and i.orgid=#{orgid}
ORDER BY i.num,i.addtime
