mapper.select:
select u.name cname, sum(CASE when score=5 then 1 else 0 end) satisfy2, sum(CASE when score=4 then 1 else 0 end) satisfy1, sum(CASE when score=3 then 1 else 0 end) satisfy, sum(CASE when score=2 then 1 else 0 end) basic, sum(CASE when score=1 then 1 else 0 end) notsatisfy, sum(CASE when score=0 then 1 else 0 end) no from evaluate e INNER JOIN sys_user u on u.guid=e.userid INNER JOIN window w on e.winid=w.guid
where 1=1
and e.addtime >= #{starttime}
and #{endtime} >= e.addtime
and e.orgid=#{orgid}
group by u.guid,u.name ORDER BY u.name

 ,'1' flag from evaluate e
where to_days(e.addtime) = to_days(now())

	and e.orgid=#{orgid}

select d.name,count(n.type) cnt,d.guid type from dic d
left JOIN num_info n on n.type=d.guid and n.isused=0
where n.orgid=#{orgid}
GROUP BY d.name,d.guid;
